import { SmeupDataStructure, SmeupDataStructureType } from "./smeupDataStructure.js";
import { SmeupObject } from "./smeupObject.js";
/**
 * SmeupTable (EXB)
 */
export interface SmeupTable extends SmeupDataStructure {
    type: SmeupDataStructureType.SmeupTable;
    columns: SmeupColumn[];
    rows: SmeupRow[];
}
/**
 * Column Data Type
 */
export interface SmeupColumn {
    /** Name (code) */
    code: string;
    /** Group */
    grp?: string;
    /** Field length */
    lun?: string;
    canonicalForm?: string;
    /** render component */
    cmp?: string;
    /**
     * I: Input
     * O: Output
     * B: Input (???)
     * H: Hide
     * E: Autocomplete
     * G: ???
     *
     */
    IO?: "I" | "O" | "B" | "H" | "G" | "E" | "";
    tooltip?: boolean;
    /** Visibile text */
    text?: string;
    sortMode?: string;
    /**
     * ASSE
     * SERIE
     */
    fill?: "ASSE" | "SERIE";
    /** Smeup object */
    ogg?: string;
    /** simple client validation */
    validation?: string;
    pfk?: string;
    tfk?: string;
    sfk?: string;
}
/**
 * Row Data Type
 */
export interface SmeupRow {
    fields: {
        [index: string]: SmeupCell;
    };
}
/** Cell */
export interface SmeupCell {
    /** name, refer to column name */
    name: string;
    /** smeup object */
    smeupObject: SmeupObject;
    tooltip?: boolean;
}
